Codelibrary(dplyr)
library(readxl)
library(ggplot2)
library(huxtable)
library(patchwork)
library(janitor)
library(stringr)

# Set Theme
theme_set(theme_minimal())
theme_update(
  axis.text = element_text(color = "black",
                           face = "bold"),
  plot.background = element_rect(fill = "#fafaf5", color = "#fafaf5"),
  panel.background = element_rect(fill = NA, color = NA),
  plot.caption = element_text(face = "italic")
)

# Preprocessing Function 
prepocess &lt;- function(file,csv = TRUE){
  library(dplyr)
  # Assuming File is a CSV
  file &lt;- read.csv(file.choose())
  # Otherwise, read as an XLSX
  if(csv == FALSE) {
    file &lt;- readxl::read_excel(file.choose())
  }
  print(str(file))

  # Clean Names
  file_cln &lt;- 
    file |&gt; 
    janitor::clean_names()

  # Skim Numeric Data
  file_cln |&gt; 
    select(where(is.numeric)) |&gt; 
    skimr::skim() |&gt;
    skimr::yank("numeric")

}

# Correlation Hux Wrapper
hux_corr &lt;- function(ct){
  if(!class(ct) == "htest"){
    print("Source must be of class 'htest'")
  } else{
    ct |&gt; 
      broom::tidy() |&gt; 
      mutate(
        `CI[LL,UU]` = paste0("[",conf.low |&gt; round(2),
                             ",",conf.high |&gt; round(2),
                             "]")
      ) |&gt; 
      rename(
        r = estimate,
        t = statistic, 
        p = p.value, 
        df = parameter,
      ) |&gt; 
      relocate(
        p, .after = df
      ) |&gt;
      relocate(
        alternative, .after = last_col()
      ) |&gt; 
      select(-conf.low,-conf.high,-method) |&gt; 
      hux() |&gt; 
      theme_article()
  }
}

