# Academic Warning: Source File had GPA and Standing switched at 120
# Remove rows containing text from GPA_FALL_2021
aw &lt;- read_excel("MJ_All_Dat.xlsx",
                 sheet = "AcademicWarningClean") |&gt; 
  clean_names()

# Academic Probation
ap &lt;- read_excel("MJ_All_Dat.xlsx",
                 sheet = "AcademicProbationClean") |&gt; clean_names()

# Freshmen
fsh &lt;- read_excel("MJ_All_Dat.xlsx",
                  sheet = "FreshmenClean") |&gt; clean_names()

